import React, { useState , useEffect} from 'react'
import axios from "axios"

export const Users = () => {

  //On utilise ici le hook useState pour preparé un variable d'etat
  //qui va stocker le fetch que l'on a fait dans notre useEffect
  const [users,setUsers] = useState([])

  useEffect(() => {
    const fetchAllUsers = async ()=>{
      try {
        //On utilise axios pour faire une requête de notre bd grâce 
        //a notre route /user avec la méthode get et stock le resultat
        //de cette requête dans la variable res.
        const res = await axios.get('http://localhost:8800/User')
        setUsers(res.data)
        console.log(res)
      } catch (error) {
        console.log(error)
      }
    }
    fetchAllUsers()
  }, [])
  

  return (
    <div>
      <h1>Dummy Users names</h1>
      <div>
        {users.map((user , key)=>(
          <div key={key}>
            {user.nom_user}
          </div>
        ))}
      </div>
    </div>
  )
}
